1=1
and (house_num like '%${search}%' or owner_name like '%${search}%' or owner_tel like '%${search}%' or owner_standby_tel like '%${search}%' or area like '%${search}%' or previous_estate_fee like '%${search}%')
and status=#{check}
and corp_id=#{corpsId}
and community_id=#{communityId}
and user_id=#{userId}

