SELECT count(*) FROM( SELECT h.house_num, h.owner_name, h.owner_tel, h.owner_standby_tel, h.area, h.previous_estate_fee, community.corp_id, cdru.community_id, cdru.user_id, IF (ISNULL(eps.id), 0, 1) status FROM house h LEFT JOIN estate_pay_statistics eps ON eps.house_id = h.house_id AND eps.year = #{year} AND eps.month = #{month} LEFT JOIN community_dept_role_user cdru ON cdru.community_id=h.community_id LEFT JOIN community ON community.community_id=h.community_id GROUP BY h.house_id ) s
