SELECT ep.*,user.name AS charge_worker_name,h.house_num FROM estate_pay ep LEFT JOIN `user` ON ep.charge_worker_id = user.user_id LEFT JOIN house h ON ep.house_id = h.house_id LEFT JOIN community ON h.community_id = community.community_id LEFT JOIN corps ON community.corp_id = corps.corp_id
ep.status = 1
AND corps.corp_id = #{corpId}
AND CONCAT_WS(',',estate_pay_num,house_num,h.owner_name,h.area,estate_unit_price,service_start_date,service_end_date,ep.pay_month,payment_amount,charge_worker_id,pay_date,ep.operator,ep.operate_time) LIKE '%${search}%'
AND CONCAT_WS(',',pay_date) LIKE '%${date}%'
AND community.community_id = #{communityId}
limit #{offset},#{rows}

