SELECT * FROM( SELECT h.house_num, h.owner_name, h.owner_tel, h.owner_standby_tel, h.area, h.previous_estate_fee, IF (ISNULL(eps.id), 0, 1) status FROM house h LEFT JOIN estate_pay_statistics eps ON eps.house_id = h.house_id AND eps.year = #{year} AND eps.month = #{month}) s
1=1
and status=#{check}

