mapper.select:
SELECT * FROM ( SELECT house.house_num, house.owner_name, house.owner_tel, house.owner_standby_tel, house.area * community.heating_unit_price heatingPrice, house.area heatingArea,
corps.corp_id, community_dept_role_user.community_id, community_dept_role_user.user_id,
IF(ISNULL(heating_pay.heating_pay_id),0,1) isStatus, IF ( ISNULL(heating_pay.heating_pay_id), CASE WHEN ( NOW() BETWEEN heating_discount_type.discount_start_date AND heating_discount_type.discount_end_date ) THEN ( house.area * community.heating_unit_price * heating_discount_type.rate ) ELSE ( house.area * community.heating_unit_price ) END , heating_pay.payment_amount ) payment_amount FROM house LEFT JOIN community ON community.community_id = house.community_id LEFT JOIN heating_pay ON heating_pay.house_id = house.house_id AND heating_pay.year=#{timeYear} LEFT JOIN heating_discount_type ON heating_discount_type.community_id = community.community_id
LEFT JOIN corps ON corps.corp_id = community.corp_id LEFT JOIN community_dept_role_user ON community_dept_role_user.community_id = community.community_id GROUP BY house.house_id ) a
1=1
and (house_num like '%${search}%' or owner_name like '%${search}%' or owner_tel like '%${search}%' or owner_standby_tel like '%${search}%' or heatingPrice like '%${search}%' or heatingArea like '%${search}%' or payment_amount like '%${search}%')
and isStatus=#{checked}
and corp_id=#{corpsId}
and community_id=#{communityId}
and user_id=#{userId}

SELECT count(*) FROM ( SELECT house.house_num, house.owner_name, house.owner_tel, house.owner_standby_tel, house.area * community.heating_unit_price heatingPrice, house.area heatingArea,
corps.corp_id, community_dept_role_user.community_id, community_dept_role_user.user_id,
IF(ISNULL(heating_pay.heating_pay_id),0,1) isStatus, IF ( ISNULL(heating_pay.heating_pay_id), CASE WHEN ( NOW() BETWEEN heating_discount_type.discount_start_date AND heating_discount_type.discount_end_date ) THEN ( house.area * community.heating_unit_price * heating_discount_type.rate ) ELSE ( house.area * community.heating_unit_price ) END , heating_pay.payment_amount ) payment_amount FROM house LEFT JOIN community ON community.community_id = house.community_id LEFT JOIN heating_pay ON heating_pay.house_id = house.house_id AND heating_pay.year=#{timeYear} LEFT JOIN heating_discount_type ON heating_discount_type.community_id = community.community_id
LEFT JOIN corps ON corps.corp_id = community.corp_id LEFT JOIN community_dept_role_user ON community_dept_role_user.community_id = community.community_id GROUP BY house.house_id ) a
1=1
and (house_num like '%${search}%' or owner_name like '%${search}%' or owner_tel like '%${search}%' or owner_standby_tel like '%${search}%' or heatingPrice like '%${search}%' or heatingArea like '%${search}%' or payment_amount like '%${search}%')
and isStatus=#{checked}
and corp_id=#{corpsId}
and community_id=#{communityId}
and user_id=#{userId}

SELECT corp_name,type,id as corpsID FROM corps
WHERE id = #{userID}
WHERE type = 0

SELECT community.community_id, community.community_name FROM community
LEFT JOIN community_dept_role_user ON community_dept_role_user.community_id = community.community_id
1=1
and community_dept_role_user.user_id=#{userId}
and corp_id =#{corpsId}

SELECT count(*) FROM ( SELECT house.house_num, house.owner_name, house.owner_tel, house.owner_standby_tel, house.area * community.heating_unit_price heatingPrice, house.area heatingArea,
corps.corp_id, community_dept_role_user.community_id, community_dept_role_user.user_id,
IF(ISNULL(heating_pay.heating_pay_id),0,1) isStatus, IF ( ISNULL(heating_pay.heating_pay_id), CASE WHEN ( NOW() BETWEEN heating_discount_type.discount_start_date AND heating_discount_type.discount_end_date ) THEN ( house.area * community.heating_unit_price * heating_discount_type.rate ) ELSE ( house.area * community.heating_unit_price ) END , heating_pay.payment_amount ) payment_amount FROM house LEFT JOIN community ON community.community_id = house.community_id LEFT JOIN heating_pay ON heating_pay.house_id = house.house_id AND heating_pay.year=#{timeYear} LEFT JOIN heating_discount_type ON heating_discount_type.community_id = community.community_id
LEFT JOIN corps ON corps.corp_id = community.corp_id LEFT JOIN community_dept_role_user ON community_dept_role_user.community_id = community.community_id GROUP BY house.house_id ) a
1=1 and isStatus=1
and corp_id=#{corpsId}
and community_id=#{communityId}
and user_id=#{userId}
