mapper.select:
SELECT * FROM ( SELECT house.house_num, house.owner_name, house.owner_tel, house.owner_standby_tel, house.area * community.heating_unit_price heatingPrice, house.area heatingArea, IF(ISNULL(heating_pay.heating_pay_id),0,1) isStatus, IF ( ISNULL(heating_pay.heating_pay_id), CASE WHEN ( NOW() BETWEEN heating_discount_type.discount_start_date AND heating_discount_type.discount_end_date ) THEN ( house.area * community.heating_unit_price * heating_discount_type.rate ) ELSE ( house.area * community.heating_unit_price ) END , heating_pay.payment_amount ) payment_amount FROM house LEFT JOIN community ON community.community_id = house.community_id LEFT JOIN heating_pay ON heating_pay.house_id = house.house_id AND heating_pay.year=#{timeYear} LEFT JOIN heating_discount_type ON heating_discount_type.community_id = community.community_id ) a
1=1
and isStatus=#{checked}

SELECT count(*) FROM ( SELECT house.house_num, house.owner_name, house.owner_tel, house.owner_standby_tel, house.area * community.heating_unit_price heatingPrice, house.area heatingArea, IF(ISNULL(heating_pay.heating_pay_id),0,1) isStatus, IF ( ISNULL(heating_pay.heating_pay_id), CASE WHEN ( NOW() BETWEEN heating_discount_type.discount_start_date AND heating_discount_type.discount_end_date ) THEN ( house.area * community.heating_unit_price * heating_discount_type.rate ) ELSE ( house.area * community.heating_unit_price ) END , heating_pay.payment_amount ) payment_amount FROM house LEFT JOIN community ON community.community_id = house.community_id LEFT JOIN heating_pay ON heating_pay.house_id = house.house_id AND heating_pay.year=#{timeYear} LEFT JOIN heating_discount_type ON heating_discount_type.community_id = community.community_id ) a
1=1
and isStatus=#{checked}

SELECT corp_name,type,id as corpsID FROM corps
WHERE id = #{userID}
WHERE type = 0

SELECT c.community_name,c.id as communityId FROM community c LEFT JOIN dept_community dc ON dc.community_id = c.corp_id LEFT JOIN role_dept_community rdc ON rdc.dept_community_id = dc.id LEFT JOIN role_dept_community_user rdcu ON rdcu.role_dept_community_id = rdc.id
1=1
and rdcu.user_id=#{userID}
