mapper.select:
SELECT need.id,need.house_need_rent_num,need.release_time,need.type, house_type.house_type_name,direction.house_direction_type_name, need.floor,area.min_area,area.max_area,area.house_area_type_name,fitment.house_fitment_type_name, price.`min_price`,price.max_price,price.house_price_range_name as rent,prices.house_price_range_name as buy,need.remark,need.customer_name,need.phone FROM house_need need LEFT JOIN house_type ON need.house_type_id=house_type.house_type_id LEFT JOIN house_direction_type direction ON need.house_direction_type_id=direction.house_direction_type_id LEFT JOIN house_area_type area ON need.house_area_type_id=area.id LEFT JOIN house_fitment_type fitment ON need.house_fitment_type_id=fitment.house_fitment_type_id LEFT JOIN house_price_range price ON need.rent_house_price_range_id=price.house_price_range_id LEFT JOIN house_price_range prices ON need.buy_house_price_range_id=prices.house_price_range_id LEFT JOIN community ON need.community_id=community.community_id LEFT JOIN corps ON community.corp_id=corps.corp_id
1=1
AND corps.corp_id=#{corp_id}
AND need.community_id=#{community_id}
AND need.type=#{type}

SELECT building FROM house LEFT JOIN community ON house.community_id=community.community_id
1=1
AND house.community_id=#{building_id}
