mapper.select:
SELECT parking_space_id,parking_num,license_plate_number,location_description, previous_parking_unit_price,car_owner_name,house_num,car_owner_tel, car_owner_standby_tel FROM parking_space LEFT JOIN house ON parking_space.house_id=house.house_id LEFT JOIN community ON house.community_id=community.community_id LEFT JOIN corps ON community.corp_id=corps.corp_id
1=1
AND corps.corp_id=#{corp_id}
AND community.community_id=#{community_id}

SELECT house_num,house_id,owner_name,owner_tel,owner_standby_tel FROM house
WHERE house_num=#{house_id}
AND community_id=#{community_id}
