mapper.select:
SELECT * FROM parking_pay a LEFT JOIN parking_space b ON a.parking_space_id = b.parking_space_id LEFT JOIN USER c ON a.charge_worker_id = c.user_id LEFT JOIN house d ON b.house_id = d.house_id
AND CONCAT_WS(',',estate_pay_num,house_num,owner_name,area,estate_unit_price,pay_month_type_id,payment_amount,a.operator,a.operate_time,account) LIKE '%${at_search}%'
limit #{offset},#{rows}

mapper.update:
UPDATE parking_pay
SET status = '0'
WHERE parking_pay_id = #{id}

mapper.select:
SELECT community_name,community_id
FROM community
WHERE corp_id = #{corp_id}

SELECT COUNT(house_num)
FROM house
WHERE community_id = #{community_id}
AND house_num = #{house_num}
